import React,{ Component } from "react";
import radium from 'radium';
//import shopcaricon from 'https://www.obdesign.com.tw/images/cart-icon.png';

const styles = {
    Header:{
        width: "96%",
        display: "flex",
        justifyContent: "flex-end",
        alignItems: "center",
        padding: "1% 3% 1% 1%"
    },
    link:{
        display:"inline-block",
        textAlign:"center",
        marginRight:"20px",
    },
    logo:{
        color:"#424242",
        width: '50px',
        fontSize:"50px",
        fontWeight:"900",
        backgroundImage: "url('https://www.obdesign.com.tw/images/cart-icon.png')",
    },
    shopcar:{
        position:"relative",
        display:"flex",
        fontSize: "45px",
    },
    shopcarIcon:{
        display:"flex",
        width: '34px',
        height: '28px',
        color: "#757575",
        backgroundImage: "url('https://www.obdesign.com.tw/images/cart-icon.png')",
        marginRight: '16px',
        lineHeight: '29px',
    },
    ShowCart_TotalCount: {
        display:"flex",
        fontSize: "10px",
    },
    shopcartext:{
        color: "#424242",
        fontSize: "12px",
    },
}

class Header extends Component{ 
    render(){ 
      return (
        <div style={styles.Header}>
            <span style={styles.logo}></span>
            <div style={styles.shopcar}>
                <div style={styles.shopcarIcon}></div>
                <p style={styles.ShowCart_TotalCount}>(0)</p>
            </div> 
        </div>
      );
    }
}


export default radium(Header);
