import Axios from 'axios';

export const getCart_DATA = 'getCart_DATA';


export function addCart(data) {
    return dispatch => {
        Axios({
            url:'http://192.168.10.184:8888/api/cart',
            method: 'POST',
            config: { 
                headers: {
                    'Content-Type': 'application/json',
                    'Access-Control-Allow-Origin': '*',
                }
            },
            data: data,
        })     
        .then((res) => {
            dispatch(getCart());
        })
        .catch((err) => {
            console.log("1111", err);
        })
    }
}

export function updateCart(data) {
    return dispatch => {
        Axios({
            url:'http://192.168.10.184:8888/api/cart',
            method: 'POST',
            config: { 
                headers: {
                    'Content-Type': 'application/json',
                    'Access-Control-Allow-Origin': '*',
                }
            },
            data: data,
        })     
        .then((res) => {
            dispatch(getCart());
        })
        .catch((err) => {
            console.log("1111", err);
        })
    }
}

export function deleteCart(data) {
    return dispatch => {
        Axios({
            url:'http://192.168.10.184:8888/api/cart',
            method: 'POST',
            config: { 
                headers: {
                    'Content-Type': 'application/json',
                    'Access-Control-Allow-Origin': '*',
                }
            },
            data: data,
        })     
        .then(() => {
            dispatch(getCart());
        })
        .catch((err) => {
            console.log("1111", err);
        })
    }
}

const getCartSuccess = data => ({
    type: getCart_DATA,
    data,
  });

  export function getCart() {
    return dispatch => {
        Axios({
            url:'http://192.168.10.184:8888/api/cart',
            method: 'GET',
            config: { 
                headers: {
                    'Content-Type': 'application/json',
                    'Access-Control-Allow-Origin': '*',
                }
            },
        })     
        .then((res) => {
            dispatch(getCartSuccess(res.data));
        })
        .catch((err) => {
            console.log("1111", err);
        })
    }
}
