let nextBuyId = 0;
export const BUY_PRODUCT = 'BUY_PRODUCT'
export const NOBUY_PRODUCT = 'NOBUY_PRODUCT'
export const CHANGENUM_PRODUCT = 'CHANGENUM_PRODUCT'

export function buyproduct(product,quantity){
    return{
        type : BUY_PRODUCT ,
        id : nextBuyId++,
        product,
        quantity
    };
}

export function nobuyproduct(id){
    return{
        type : NOBUY_PRODUCT ,
        id
    };
}

export function changenumproduct(id,quantity){
    return{
        type : CHANGENUM_PRODUCT ,
        id,
        quantity
    };
}
