import { connect } from './node_modules/react-redux';
import { buyproduct } from '../action/index.js';
import ProductList from '../component/ProductList.js';

const mapStateToProps = (state) => {
  return {
    products: state.postByProduct
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    onPrdClick: (product,num) => { dispatch(buyproduct(product,num)) }
  }
}

const VisiblePrdList = connect(
  mapStateToProps,
  mapDispatchToProps
)(ProductList)

export default VisiblePrdList;
