import React from 'react';
import { Row, Col, Button } from 'antd';

const style ={
    img:{
        width:"30%"
    },
    origtext:{
        textDecoration: "line-through",
        color: "#757575",
        fontSize: "15px",
        textAlign: "right",
    },
    selltext:{
        color: "#c2185b",
        fontSize: "21px",
    },
    smalltext:{
        fontSize: "60%",
        verticalAlign: "super",
    },
    titletext:{
        fontSize:"12px",
        color:"#424242",
    },
    nametext:{
        fontSize:"15px",
        color:"#0097a7",
    },
}
const Product = ({ product, onClick }) => (
    <Col md={6} sm={8} xs={12}>
        <div>
          <img alt="載入失敗" src={"https://image.obdesign.com.tw/catalog/products/AB9900/AB9900@CB-1-1_70.jpg"} style={style.img}/>
        </div>
        <div style={{padding:"8px"}}>
            <div style={style.titletext}>
            抓褶下襬連袖前短後長上衣
            </div>
            <div style={style.nametext}>
            抓褶下襬連袖前短後長上衣
            </div>
            <Row type="flex" justify="space-between" align="middle" style={{marginTop:"15px"}}>
                <Col>
                    <Button onClick={onClick}>購買</Button>
                </Col>
                <Col>
                    <div style={style.selltext}>
                        <span style={style.smalltext}>團購價</span>
                        290
                    </div>
                    <div style={style.origtext}>
                        <span style={style.smalltext}>市價</span>
                        390
                    </div>
                </Col>
            </Row>
        </div>
    </Col>
)

export default Product;
