import {getCart_DATA} from '../action/ShopCar'

const ShopCar = (state=[],action) => {
    switch (action.type){
        case 'NOBUY_PRODUCT':
            return state.filter(state => state.id !== action.id);
        case 'CHANGENUM_PRODUCT':
            return state.map(odddata=>{
                if(odddata.id !== action.id)
                    return odddata;
                return {...odddata, quantity: action.quantity}
            });
        case getCart_DATA:
            return [
                ...state,
                {
                    id: action.id,
                    product: {...action.product},
                    quantity: action.quantity
                }
            ];
        default:
            return state;
    }
}

export default ShopCar;
