import { API_REQUEST } from 'redux-middleware-fetch';
import Axios from 'axios';

export const getProductDate_DATA = 'Product/getProduct_DATA';

export function getProductDate(data) {
    return dispatch => {
        Axios({
            url:'http://192.168.10.182:8888/api/product',
            method: 'post',
            config: { headers: { 'Content-Type': 'application/json' } },
        })     
        .then((res) => {
                dispatch(getProductDateSuccess(res.data.message));
        })
    }
}
