import React, {Component} from 'react'
import { Table } from 'antd';
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import { updateCart, deleteCart } from '../action/ShopCar';


const styles = {
    table:{
        maxWidth:"1400px",
        width:"90%",
        margin:"0 auto",
    },
    antTablePlaceholder: {
        marginTop: '10px',
    },
    changenumblock:{
        display:"flex",
        alignItems:"center",
        justifyContent:"center",
    },
    nobuyicon:{
        fontSize:"25px",
        cursor:"pointer",
        color: '#bdbdbd',
    },
    changenumicon:{
        fontSize:"20px",
        cursor:"pointer",
        color: '#f79db4',
    },
    numtext:{
        display:"flex",
        justifyContent:"flex-end",
    },
    img:{
        maxWidth:"200px",
        width: "100%",
    },
    titletext:{
        fontSize:"12px",
        color:"#424242",
    },
    nametext:{
        fontSize:"15px",
        color:"#424242",
    },
}

const columns = [{
    title: '商品名稱',
    dataIndex: 'name',
    sorter: (a, b) => a.productName > b.productName,
}, {
    title: '商品圖片',
    dataIndex: 'image',
    align:'center',
    width:"210px"
}, {
    title: '單價',
    dataIndex: 'price',
    align:'center',
    width:"70px"
},{
    title: '數量',
    dataIndex: 'prdquantity',
    align:'center',
    width:"75px",
},{
    title: '小計',
    dataIndex: 'prdsubtotal',
    align:'center',
    width:"85px"
},{
    title: '移除',
    dataIndex: 'prdnobuy',
    align:'center',
    width:"65px"
}

];

class ShopCarList extends Component{
    updateShopCart(product){
        const { updateCart } = this.props;
        const data = {
           idCart: product.idCart,
           name: product.name,
           accountName: 'Huang Eva',
           discount: product.distcount,
           quantity: 1,
           size: 'M',   
           image: product.image,   
        };
        updateCart(data);
      }
      deleteShopCart(product){
        const { deleteCart } = this.props;
        // const data = {
        //    idCart: product.idCart,
        //    name: product.name,
        //    accountName: 'Huang Eva',
        //    discount: product.distcount,
        //    quantity: 1,
        //    size: 'M',   
        //    image: product.image,   
        // };
        console.log('111', product);
        deleteCart(product.idCart);
      }
    render(){ 
        //console.log('getCartDate',this.props.shopping,this.props.tt);
        let dataSourceTable =[];
        let TotalMoney =0;
        // console.log('this.props.shopping',this.props.shopping);
        // console.log(typeof this.props.shopping);
        this.props.shopping.data && this.props.shopping.data.map(shop=>{
            let dataSourceTableObj = {};
            const subtoal = parseInt(shop.discount) * parseInt(shop.quantity);
            dataSourceTableObj.key = shop.id;
            dataSourceTableObj.name = <div>
                <div style={styles.nametext}>{shop.name}</div>
            </div>;
            dataSourceTableObj.image = <img style={styles.img} src={shop.image} alt="圖片載入失敗"/>;
            dataSourceTableObj.price = <div>{shop.distcount}</div>;
            dataSourceTableObj.prdquantity = <div style={styles.changenumblock}>
                <i 
                    style={styles.changenumicon}
                    className="far fa-minus-square"
                    onClick={()=>this.updateShopCart(shop)}
                />
                <div style={{margin:"0 5px"}}>{shop.quantity}</div>
                <i 
                    style={styles.changenumicon}
                    className="far fa-plus-square"
                    onClick={()=>this.updateShopCart(shop)}
                />
            </div>;
            dataSourceTableObj.prdsubtotal = <div>{subtoal}</div>;
            dataSourceTableObj.prdnobuy = <i 
                style={styles.nobuyicon}
                className="fas fa-trash-alt"
                onClick={() => this.deleteShopCart(shop.id)}
            />;
            TotalMoney += subtoal;
            dataSourceTable.push(dataSourceTableObj);
        })      
      return (
        <Table
            columns={columns}
            dataSource={dataSourceTable}
            bordered
            scroll={{x:800}}
            footer={() => <div style={styles.numtext}>{`總計：${TotalMoney}`}</div>}
            style={styles.table}
        />
      );
    }
  }

  const mapStateToProps = (state) => {
    return {
        shopping: state.ShopCar.getCartDate,
        tt: state,
      }
  }

  const mapDispatchToProps = dispatch => bindActionCreators({
    updateCart,
    deleteCart,
  }, dispatch);

  export default connect(
    mapStateToProps,
    mapDispatchToProps,
)(ShopCarList);
