import React from 'react'
import { Row } from 'antd';
import Product from './Product.js'

const style = {
    maxWidth:"1100px",
    width:"100%",
    margin:"0 auto",
}
const ProductList = ({ products, onPrdClick }) => (
    <Row type="flex" gutter={24} align="top" style={style}>
        {products && products.commodity.map(product=>
            <Product
                key={product.id}
                product = {product}
                onClick={()=>onPrdClick(product,1)}
            />
        )}
    </Row>
)

export default ProductList;
