import React,{Component} from 'react';
import radium from 'radium';
//import banner from 'https://obcdn4.obdesign.com.tw/images/ObdesignBanner/WOMEN/images/190524-AB6701-1920x820_v1.jpg';

const style = {
    wrapper:{
        width: '100%',
        padding: 0,
        margin: 0,
    },
    banner: {
        width: '100%',
        Max: '820px',
        padding: 0,
        margin: 0,
    }
}

class Banner extends Component{ 
    render(){ 
      return (
        <div style={style.wrapper}>
            <img 
            src='https://obcdn4.obdesign.com.tw/images/ObdesignBanner/WOMEN/images/190524-AB6701-1920x820_v1.jpg' 
            alt="banner" 
            style={style.banner} />
        </div>
      );
    }
  }





export default radium(Banner);
