import React, {Component} from 'react'
import { Table } from 'antd';

const styles = {
    table:{
        maxWidth:"1400px",
        width:"100%",
        margin:"0 auto",
    },
}

const columns = [{
    title: '商品名稱',
    dataIndex: 'prdname',
    sorter: (a, b) => a.prdname > b.prdname,
}, {
    title: '商品圖片',
    dataIndex: 'prdimg',
    align:'center',
    width:"210px"
}, {
    title: '單價',
    dataIndex: 'prdsellprice',
    align:'center',
    width:"70px"
},{
    title: '數量',
    dataIndex: 'prdquantity',
    align:'center',
    width:"75px",
},{
    title: '小計',
    dataIndex: 'prdsubtotal',
    align:'center',
    width:"85px"
},{
    title: '移除',
    dataIndex: 'prdnobuy',
    align:'center',
    width:"65px"
}

];

class ShopCarList extends Component{
    render(){ 
        let dataSourceTable =[];
        let TotalMoney =0;        
      return (
        <Table
                columns={columns}
                dataSource={dataSourceTable}
                bordered
                scroll={{x:800}}
                footer={() => <div style={styles.numtext}>{`總計：${TotalMoney}`}</div>}
                style={styles.table}
        />
      );
    }
  }

export default ShopCarList;
