import React,{Component} from 'react';
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import Product from './Product.js';
import { getProductDate } from '../action/Product';
import { buyproduct } from '../action/ShopCar';

const styles = {
    wrapper:{
        width: '100%',
        padding: 0,
        margin: 0,
    },
    title: {
      width: '100%',
      display: 'flex',
    },
    titleText: {
        width: '30%',
        textAlign: 'center',
        fontSize: 28,
        lineHeight: '86px',
    },
    line: {
        width: '35%',
        padding: '35px 0',
    },
    proItem:{
      display: 'flex',
      //width: '20%',
      height: 'auto',
      flexDirection: 'row',
      padding: 0,
      margin: 0,
      flexFlow: 'wrap',
      '@media (max-width: 1023px)': {
          width: '33%',
        },
      '@media (max-width: 414px)': {
          width: '50%',
        },
  },
}


class ProductList extends Component{
    constructor(props){
        super(props)
        this.props.getProductDate();
    }
    render(){
      const {productListDate}=this.props;
      const {buyproduct}=this.props;
      console.log('productListDate',productListDate);
      //this.props.foo   兩種寫法都可以
      return (
        <div style={styles.wrapper}>
          <div style={styles.title}>
              <div style={styles.line}><hr></hr></div><p style={styles.titleText}>YOU MUST HAVE!</p><div style={styles.line}><hr></hr></div>
          </div> 
          <div style={styles.proItem}>
          {productListDate && productListDate.map((data, key)=>
            <Product 
              key={key} 
              product = {data}
              onClick = {() => buyproduct(data,1)}
            />
          )}
          </div>
        </div>
      );
    }
  }

  const mapStateToProps = (state) => ({
    productListDate:state.Product.getProductDate.data,
  });

  const mapDispatchToProps = dispatch => bindActionCreators({
    getProductDate,
    buyproduct
  }, dispatch);


export default connect(
    mapStateToProps,
    mapDispatchToProps
)(ProductList);
