import Axios from 'axios';
//let nextBuyId = 0;
//export const BUY_PRODUCT = 'BUY_PRODUCT'
export const NOBUY_PRODUCT = 'NOBUY_PRODUCT'
export const CHANGENUM_PRODUCT = 'CHANGENUM_PRODUCT'
export const getCart_DATA = 'getCart_DATA';
export const deleteCart_Success = 'deleteCart_Success';
export const updateCart_Success = 'deleteCart_Success';



// export function buyproduct(product,quantity){
//     return{
//         type : BUY_PRODUCT ,
//         id : nextBuyId++,
//         product,
//         quantity
//     };
// }
export function nobuyproduct(id){
    return{
        type : NOBUY_PRODUCT ,
        id
    };
}
export function changenumproduct(id,quantity){
    return{
        type : CHANGENUM_PRODUCT ,
        id,
        quantity
    };
}

export function addCart(data) {
    console.log('data',data);
    return dispatch => {
        Axios({
            url:'http://192.168.10.186:8888/api/cart',
            method: 'POST',
            data: data,
            config: {
            headers: { 
                'content-type': 'application/json',
            },
            }
        })
        .then((res) => {
            console.log('123123123');
            dispatch(getCart());
        })
        .catch((err) => {
            console.log("1111", err);
        })
    }
}

const updateCartSuccess = data => ({
    type: updateCart_Success,
    data,
  });

export function updateCart(id) {
    return dispatch => {
        Axios({
            url:`http://192.168.10.186:8888/api/cart/${id}`,
            method: 'PUT',
            headers: { 
                'content-type': 'application/json',
            },
        })
        .then((res) => {
            dispatch(updateCart());
        })
        .catch((err) => {
            console.log("1111", err);
        })
    }
}

const deleteCartSuccess = data => ({
    type: deleteCart_Success,
    data,
  });

export function deleteCart(id) {
    return dispatch => {
        Axios({
            url:`http://192.168.10.186:8888/api/cart/${id}`,
            method: 'DELETE',
            config: { 
                headers: {
                    'Content-Type': 'application/json',
                    'Access-Control-Allow-Origin': '*',
                }
            },
        })     
        .then((res) => {
            if (res.data.statusCode === 0) {
                alert('刪除成功');
                dispatch(deleteCartSuccess());
            } else {
                alert('刪除失敗');
            }
        })
        .catch((err) => {
            console.log("1111", err);
        })
    }
}

const getCartSuccess = data => ({
    type: getCart_DATA,
    data,
  });

export function getCart() {
return dispatch => {
    Axios({
        url:'http://192.168.10.186:8888/api/cart',
        method: 'get',
        config: { 
            headers: {
                'Content-Type': 'application/json',
                'Access-Control-Allow-Origin': 'http://192.168.10.186:8888/api/cart',
            }
        },
    })     
    .then((res) => {
        console.log(res);
        dispatch(getCartSuccess(res.data));
    })
    .catch((err) => {
        console.log("1111", err);
    })
}
}
