import React from 'react';
import { Button } from 'antd';

const style ={
    wrapper:{
        width: '100%',
    },
    proItem:{
        display: 'flex',
        flex: 1,
        width: '25%',
        height: 'auto',
        flexDirection: 'column',
    },
    img:{
        width: "100%"
    },
    origtext:{
        textDecoration: "line-through",
        color: "#757575",
        fontSize: "15px",
        textAlign: "right",
        flex: '1 1 30%',
        display: 'flex',
        justifyContent: 'flex-end',
    },
    selltext:{
        color: "rgb(234, 47, 121)",
        fontSize: "21px",
        flex: '1 1 30%',
        display: 'flex',
        justifyContent: 'flex-end',
    },
    smalltext:{
        fontSize: "60%",
        verticalAlign: "super",
    },
    titletext:{
        fontSize:"12px",
        color:"#424242",
    },
    nametext:{
        fontSize: "15px",
        color: "rgb(234, 47, 121)",
        flex: '1 1 50%',
        display: 'flex',
    },
    button: {
        flex: '1 1 50%',
        display: 'flex',
        margin:'0 5px 15px'
    },
    protext: {
        width: '100%',
        display: 'flex',
        flexFlow: 'wrap',
    }
}
const Product = ({ product, onClick }) => (
    <div style={style.wrapper}>
        <div style={style.proItem}>
            <div>
            <img alt="載入失敗" src={"https://image.obdesign.com.tw/catalog/products/AB9900/AB9900@CB-1-1_70.jpg"} style={style.img}/>
            </div>
            <div style={style.protext}>
                <div style={style.nametext}>
                    抓褶下襬連袖前短後長上衣
                </div>
                <div style={style.origtext}>
                    <span style={style.smalltext}>NT$</span>
                        390
                </div>
                <div  style={style.button}>
                <Button onClick={onClick}>購買</Button>
                </div>
                <div style={style.selltext}>
                    <span style={style.smalltext}>NT$</span>
                        290
                </div>
            </div>
        </div>
    </div>
)

export default Product;
