import React from "react";
import radium from 'radium';

const styles = {
    Header:{
        width: "96%",
        //margin: "0 auto",
        display: "flex",
        justifyContent: "flex-end",
        alignItems: "center",
        padding: "1% 3% 1% 1%"
    },
    link:{
        display:"inline-block",
        textAlign:"center",
        marginRight:"20px",
    },
    producttext:{
        color:"#424242",
        fontSize:"50px",
        fontWeight:"900",
    },
    shopcar:{
        position:"relative",
        display:"inline-block",
    },
    shopcaricon:{
        color:"#757575",
        fontSize:"45px",
    },
    shopcarnum:{
        position: "absolute",
        background: "rgba(255,0,0,0.6)",
        borderRadius: "50%",
        top: "11%",
        left: "34%",
        width: "25px",
        height: "25px",
        color: "#FFF",
        fontWeight: "900",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
    },
    shopcartext:{
        color: "#424242",
        fontSize: "12px",
    },
}

const Header = (shopcarlen) => (
    <div style={styles.Header}>
            <span style={styles.producttext}>商品</span>

            <div style={styles.shopcar}>
                <i className="fas fa-shopping-cart" style={styles.shopcaricon}></i>
                 {shopcarlen.shopcarlen >0 ? <span style={styles.shopcarnum}>{shopcarlen.shopcarlen}</span>:""}
            </div>
            <div style={styles.shopcartext}>購物車</div>
    </div>
)


export default radium(Header);
