const Product = (state=[],action) => {
    switch (action.type){
        case 'BUY_PRODUCT':
            return{
                id: action.id,
                product: {...action.product},
                quantity: action.quantity
            };
        case 'NOBUY_PRODUCT':
            return Object.assign([], state).filter(state=>{
                return state.id !== action.id
            })
        case 'CHANGENUM_PRODUCT':
            if(state.id !== action.id)
                return state;
            return Object.assign({},state,{
                quantity: action.quantity
            })
        default:
            return state;
    }
}

const BuyProduct = (state=[],action) => {
    switch (action.type){
        case 'BUY_PRODUCT':
            if(state.map(t => {return t.product.id}).indexOf(action.product.id) >= 0){
                alert("此商品已加入購物車");
                return state;
            }
            else
                return [
                    ...state,
                    Product(state,action)
                ];
        case 'NOBUY_PRODUCT':
            return Product(state,action);
        case 'CHANGENUM_PRODUCT':
            return state.map(t=>Product(t,action));
        default:
            return state;
    }
}

export default BuyProduct;
