import React,{ Component } from 'react';
import { Button } from 'antd';
import radium from 'radium';
//import { bindActionCreators } from 'redux';
import { buyproduct } from '../action/index.js';
import { connect } from 'react-redux';
//import { addCart } from '../action/ShopCar';

const style = {
    wrapper:{
       width: '18%',
       margin: '0 1% 3% 1%',
       display: 'flex',
       flexFlow: 'wrap',
       '@media (max-width: 1100px)': {
        width: '23%',
      },
      '@media (max-width: 860px)': {
        width: '31.3%',
      },
      '@media (max-width: 580px)': {
        width: '48%',
      },
    },
    proItem:{
        display: 'flex',
        width: '100%', 
        height: 'auto',
        padding: 0,
        margin: 0,
        flexDirection: 'column',
    },
    img:{
        width: "100%"
    },
    origtext:{
        textDecoration: "line-through",
        color: "#757575",
        fontSize: "16px",
        textAlign: "right",
        flex: '1 1 30%',
        display: 'flex',
        justifyContent: 'flex-end',
        '@media (max-width: 1023px)': {
            fontSize: "14px",
          },
        '@media (max-width: 414px)': {
            fontSize: "12px",
          },
    },
    selltext:{
        color: "rgb(234, 47, 121)",
        fontSize: "18px",
        flex: '1 1 30%',
        display: 'flex',
        justifyContent: 'flex-end',

        margin:'10px 0',
        '@media (max-width: 1023px)': {
            fontSize: "14px",
          },
        '@media (max-width: 414px)': {
            fontSize: "12px",
          },
    },
    smalltext:{
        fontSize: "60%",
        verticalAlign: "super",
    },
    nametext:{
        fontSize: "16px",
        color: "#333",
        flex: '1 1 70%',
        display: 'flex',
        '@media (max-width: 1023px)': {
            fontSize: "14px",
          },
        '@media (max-width: 414px)': {
            fontSize: "12px",
          },
    },
    button: {
        flex: '1 1 50%',
        display: 'flex',
        margin:'10px 5px 15px',

    },
    protext: {
        width: '94%',
        display: 'flex',
        flexFlow: 'wrap',
        padding: '3%',
    }
}

class Product extends Component{

    render(){
      const {
          product,
          onClick,
      }=this.props;
      return (
        <div style={style.wrapper}>
            <div style={style.proItem}>
                <img alt="載入失敗" src={product.prdimg} style={style.img}/>
                <div style={style.protext}>
                    <div style={style.nametext}>
                    {product.prdname}
                    </div>
                    <div style={style.origtext}>
                        <span style={style.smalltext}>NT$</span>
                        {product.prdorigprice}
                    </div>
                    <div  style={style.button}>
                    <Button onClick={onClick}>加入購物車</Button>
                    </div>
                    <div style={style.selltext}>
                        <span style={style.smalltext}>NT$</span>
                        {product.prdsellprice}
                    </div>
                </div>
            </div>
        </div>

      );
    }
}

const mapStateToProps = (state) => {
    return {
      products: state.postByProduct
    }
  }

  const mapDispatchToProps = (dispatch) => {
    return {
      onPrdClick: (product,num) => { dispatch(buyproduct(product,num)) }
    }
  }

export default connect(mapStateToProps,mapDispatchToProps)(radium(Product));
