import { connect } from './node_modules/react-redux';
import { nobuyproduct, changenumproduct } from '../action/index.js';
import ShopCarList from '../component/ShopCarList.js';


const mapStateToProps = (state) => {
  return {
    shopping: state.BuyProduct,
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    changenum: (id,num) => { dispatch(changenumproduct(id,num)) },
    nobuy: (id) => {dispatch(nobuyproduct(id))},
  }
}

const VisibleShopcarList = connect(
  mapStateToProps,
  mapDispatchToProps
)(ShopCarList)

export default VisibleShopcarList;
