import React, {Component} from 'react'
import { Table } from 'antd';
import { connect } from 'react-redux';
import { nobuyproduct, changenumproduct } from '../action/ShopCar';

const styles = {
    table:{
        maxWidth:"1400px",
        width:"90%",
        margin:"0 auto",
    },
    changenumblock:{
        display:"flex",
        alignItems:"center",
        justifyContent:"center",
    },
    nobuyicon:{
        fontSize:"25px",
        cursor:"pointer",
    },
    changenumicon:{
        fontSize:"20px",
        cursor:"pointer",
    },
    numtext:{
        display:"flex",
        justifyContent:"flex-end",
    },
    img:{
        maxWidth:"200px",
        width: "100%",
    },
    titletext:{
        fontSize:"12px",
        color:"#424242",
    },
    nametext:{
        fontSize:"15px",
        color:"#424242",
    },
}

const columns = [{
    title: '商品名稱',
    dataIndex: 'prdname',
    sorter: (a, b) => a.prdname > b.prdname,
}, {
    title: '商品圖片',
    dataIndex: 'prdimg',
    align:'center',
    width:"210px"
}, {
    title: '單價',
    dataIndex: 'prdsellprice',
    align:'center',
    width:"70px"
},{
    title: '數量',
    dataIndex: 'prdquantity',
    align:'center',
    width:"75px",
},{
    title: '小計',
    dataIndex: 'prdsubtotal',
    align:'center',
    width:"85px"
},{
    title: '移除',
    dataIndex: 'prdnobuy',
    align:'center',
    width:"65px"
}

];

class ShopCarList extends Component{
    CalculationNum(type,quantity){
        switch(type){
            case "decrease":
                if(quantity <= 1){
                    alert('最少為1')
                    return quantity
                }
                return quantity-1
            case "increase":
                return quantity+1
            default:
                return quantity
        }
    }
    render(){ 
        let dataSourceTable =[];
        let TotalMoney =0;  
        this.props.shopping.map(shop=>{
            let dataSourceTableObj = {};
            const subtoal = parseInt(shop.product.prdsellprice) * parseInt(shop.quantity);
            dataSourceTableObj.key = shop.id;
            dataSourceTableObj.prdname = <div>
                <div style={styles.titletext}>{shop.product.prdtitle}</div>
                <div style={styles.nametext}>{shop.product.prdname}</div>
            </div>;
            dataSourceTableObj.prdimg = <img style={styles.img} src={shop.product.prdimg} alt="圖片載入失敗"/>;
            dataSourceTableObj.prdsellprice = <div>{shop.product.prdsellprice}</div>;
            dataSourceTableObj.prdquantity = <div style={styles.changenumblock}>
                <i 
                    style={styles.changenumicon}
                    className="far fa-minus-square"
                    onClick={()=>this.props.changenum(shop.id,this.CalculationNum("decrease",shop.quantity))}
                />
                <div style={{margin:"0 5px"}}>{shop.quantity}</div>
                <i 
                    style={styles.changenumicon}
                    className="far fa-plus-square"
                    onClick={()=>this.props.changenum(shop.id,this.CalculationNum("increase",shop.quantity))}
                />
            </div>;
            dataSourceTableObj.prdsubtotal = <div>{subtoal}</div>;
            dataSourceTableObj.prdnobuy = <i 
                style={styles.nobuyicon}
                className="fas fa-trash-alt"
                onClick={() => this.props.nobuy(shop.id)}
            />;
            TotalMoney += subtoal;
            dataSourceTable.push(dataSourceTableObj);
        })      
      return (
        <Table
                columns={columns}
                dataSource={dataSourceTable}
                bordered
                scroll={{x:800}}
                footer={() => <div style={styles.numtext}>{`總計：${TotalMoney}`}</div>}
                style={styles.table}
        />
      );
    }
  }

  const mapStateToProps = (state) => {
    return {
      shopping: state.BuyProduct,
    }
  }

  const mapDispatchToProps = (dispatch) => {
    return {
      changenum: (id,num) => { dispatch(changenumproduct(id,num)) },
      nobuy: (id) => {dispatch(nobuyproduct(id))},
    }
  }


  export default connect(
    mapStateToProps,
    mapDispatchToProps
)(ShopCarList);
