import React,{ Component } from 'react';
import { Button } from 'antd';
//import radium from 'radium';

const style = {
    wrapper:{
       // width: '20%',
    },
    proItem:{
        display: 'flex',
        width: '20%',
        height: 'auto',
        padding: 0,
        margin: 0,
        flexDirection: 'column',
    },
    img:{
        width: "100%"
    },
    origtext:{
        textDecoration: "line-through",
        color: "#757575",
        fontSize: "16px",
        textAlign: "right",
        flex: '1 1 30%',
        display: 'flex',
        justifyContent: 'flex-end',
        '@media (max-width: 1023px)': {
            fontSize: "14px",
          },
        '@media (max-width: 414px)': {
            fontSize: "12px",
          },
    },
    selltext:{
        color: "rgb(234, 47, 121)",
        fontSize: "18px",
        flex: '1 1 30%',
        display: 'flex',
        justifyContent: 'flex-end',

        margin:'10px 0',
        '@media (max-width: 1023px)': {
            fontSize: "14px",
          },
        '@media (max-width: 414px)': {
            fontSize: "12px",
          },
    },
    smalltext:{
        fontSize: "60%",
        verticalAlign: "super",
    },
    nametext:{
        fontSize: "16px",
        color: "#333",
        flex: '1 1 70%',
        display: 'flex',
        '@media (max-width: 1023px)': {
            fontSize: "14px",
          },
        '@media (max-width: 414px)': {
            fontSize: "12px",
          },
    },
    button: {
        flex: '1 1 50%',
        display: 'flex',
        margin:'10px 5px 15px',

    },
    protext: {
        width: '94%',
        display: 'flex',
        flexFlow: 'wrap',
        padding: '3%',
    }
}

class Product extends Component{ 
    render(){ 
      const {product}=this.props;
      return (
        <div style={style.wrapper}>
            <div style={style.proItem}>
                <img alt="載入失敗" src={product.image} style={style.img}/>
                <div style={style.protext}>
                    <div style={style.nametext}>
                    {product.name}
                    </div>
                    <div style={style.origtext}>
                        <span style={style.smalltext}>NT$</span>
                        {product.price}
                    </div>
                    <div  style={style.button}>
                    <Button>加入購物車</Button>
                    </div>
                    <div style={style.selltext}>
                        <span style={style.smalltext}>NT$</span>
                        {product.distcount}
                    </div>
                </div>
            </div>
        </div>

      );
    }
}



export default Product;
