//import { API_REQUEST } from 'redux-middleware-fetch';
import Axios from 'axios';

export const getProductDate_DATA = 'Product/getProduct_DATA';
export const addCart_DATA = 'Product/addCart_DATA';

const getProductDateSuccess = data => ({
    type: getProductDate_DATA,
    data,
  });

export function getProductDate() {
    return dispatch => {
        Axios({
            url:'http://192.168.10.184:8888/api/product',
            method: 'get',
            config: {
                headers: {
                    'Content-Type': 'application/json',
                    'Access-Control-Allow-Origin': '*',
                }
            },
        })     
        .then((res) => {
            dispatch(getProductDateSuccess(res.data));
        })
        .catch((err) => {
            console.log("1111", err);
        })
    }
}


const addCartSuccess = data => ({
    type: addCart_DATA,
    data,
  });

export function addCart(data) {
    return dispatch => {
        Axios({
            url:'http://192.168.10.184:8888/api/product',
            method: 'POST',
            config: { 
                headers: {
                    'Content-Type': 'application/json',
                    'Access-Control-Allow-Origin': '*',
                }
            },
            body: data,
        })     
        .then((res) => {
            dispatch(addCartSuccess(res.data));
        })
        .catch((err) => {
            console.log("1111", err);
        })
    }
}
