import React, {Component} from 'react'
import { Table } from 'antd';

const styles = {
    table:{
        maxWidth:"1400px",
        width:"90%",
        margin:"0 auto",
    },
    changenumblock:{
        display:"flex",
        alignItems:"center",
        justifyContent:"center",
    },
    nobuyicon:{
        fontSize:"25px",
        cursor:"pointer",
    },
    changenumicon:{
        fontSize:"20px",
        cursor:"pointer",
    },
    numtext:{
        display:"flex",
        justifyContent:"flex-end",
    },
    img:{
        maxWidth:"200px",
        width: "100%",
    },
    titletext:{
        fontSize:"12px",
        color:"#424242",
    },
    nametext:{
        fontSize:"15px",
        color:"#424242",
    },
}

const columns = [{
    title: '商品名稱',
    dataIndex: 'prdname',
    sorter: (a, b) => a.prdname > b.prdname,
}, {
    title: '商品圖片',
    dataIndex: 'prdimg',
    align:'center',
    width:"210px"
}, {
    title: '單價',
    dataIndex: 'prdsellprice',
    align:'center',
    width:"70px"
},{
    title: '數量',
    dataIndex: 'prdquantity',
    align:'center',
    width:"75px",
},{
    title: '小計',
    dataIndex: 'prdsubtotal',
    align:'center',
    width:"85px"
},{
    title: '移除',
    dataIndex: 'prdnobuy',
    align:'center',
    width:"65px"
}

];

class ShopCarList extends Component{
    CalculationNum(type,quantity){
        switch(type){
            case "decrease":
                if(quantity <= 1){
                    alert('最少為1')
                    return quantity
                }
                return quantity-1
            case "increase":
                return quantity+1
            default:
                return quantity
        }
    }
    render(){ 
        let dataSourceTable =[];
        let TotalMoney =0;        
      return (
        <Table
                columns={columns}
                dataSource={dataSourceTable}
                bordered
                scroll={{x:800}}
                footer={() => <div style={styles.numtext}>{`總計：${TotalMoney}`}</div>}
                style={styles.table}
        />
      );
    }
  }

export default ShopCarList;
