import React from 'react';
import { Button } from 'antd';
import radium from 'radium';

const style = {
    wrapper:{
        width: '100%',
        padding: 0,
        margin: 0,
    },
    title: {
        display: 'flex',
        flexDirection: 'row',
    },
    hr: {
        flex: '1 1 33%',
    },
    titleText: {
        flex: '1 1 33%',
    },
    proItem:{
        display: 'flex',
        flex: 1,
        width: '20%',
        height: 'auto',
        flexDirection: 'column',
        padding: 0,
        margin: 0,
        '@media (max-width: 1023px)': {
            width: '33%',
          },
        '@media (max-width: 414px)': {
            width: '50%',
          },
    },
    img:{
        width: "100%"
    },
    origtext:{
        textDecoration: "line-through",
        color: "#757575",
        fontSize: "16px",
        textAlign: "right",
        flex: '1 1 30%',
        display: 'flex',
        justifyContent: 'flex-end',
        '@media (max-width: 1023px)': {
            fontSize: "14px",
          },
        '@media (max-width: 414px)': {
            fontSize: "12px",
          },
    },
    selltext:{
        color: "rgb(234, 47, 121)",
        fontSize: "18px",
        flex: '1 1 30%',
        display: 'flex',
        justifyContent: 'flex-end',

        margin:'10px 0',
        '@media (max-width: 1023px)': {
            fontSize: "14px",
          },
        '@media (max-width: 414px)': {
            fontSize: "12px",
          },
    },
    smalltext:{
        fontSize: "60%",
        verticalAlign: "super",
    },
    nametext:{
        fontSize: "16px",
        color: "#333",
        flex: '1 1 70%',
        display: 'flex',
        '@media (max-width: 1023px)': {
            fontSize: "14px",
          },
        '@media (max-width: 414px)': {
            fontSize: "12px",
          },
    },
    button: {
        flex: '1 1 50%',
        display: 'flex',
        margin:'10px 5px 15px',

    },
    protext: {
        width: '94%',
        display: 'flex',
        flexFlow: 'wrap',
        padding: '3%',
    }
}
const Product = ({ product, onClick }) => (
    <div style={style.wrapper}>
        <div style={style.title}>
            <hr></hr><p style={style.titleText}>YOU MUST HAVE!</p><hr style={style.hr}></hr>
        </div> 
        <div style={style.proItem}>
            <img alt="載入失敗" src={"https://image.obdesign.com.tw/catalog/products/AB9900/AB9900@CB-1-1_70.jpg"} style={style.img}/>
            <div style={style.protext}>
                <div style={style.nametext}>
                    抓褶下襬連袖前短後長上衣
                </div>
                <div style={style.origtext}>
                    <span style={style.smalltext}>NT$</span>
                        390
                </div>
                <div  style={style.button}>
                <Button onClick={onClick}>加入購物車</Button>
                </div>
                <div style={style.selltext}>
                    <span style={style.smalltext}>NT$</span>
                        290
                </div>
            </div>
        </div>
    </div>
)

export default radium(Product);
