import React,{ Component } from "react";
import radium from 'radium';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';

const styles = {
    Header:{
        width: "96%",
        display: "flex",
        justifyContent: "flex-end",
        alignItems: "flex-end",
        padding: "1% 3% 1% 1%",
    },
    logo:{
        display: "flex",
        width: '170px',
        height: '68px',
        fontSize: "50px",
        fontWeight: "900",
        backgroundImage: "url('https://www.obdesign.com.tw/images/logo.png')",
        backgroundRepeat: 'no-repeat',
    },
    shopcar:{
        position: "relative",
        display: "flex",
        fontSize: "18px",
    },
    shopcarIcon:{
        display: "flex",
        width: '34px',
        height: '28px',
        color: "#757575",
        backgroundImage: "url('https://www.obdesign.com.tw/images/cart-icon.png')",
        marginRight: '5px',
        lineHeight: '29px',
    },
    ShowCart_TotalCount: {
        display: "flex",
        fontSize: "18px",
    },
}

class Header extends Component{ 
    render(){ 
    const {shopcarlen}=this.props;
      return (
        <div style={styles.Header}>
            <Link to='/' key='0'>
                <span style={styles.logo}></span>
            </Link>
            <div style={styles.shopcar}>
                <Link to='/SHOPCAR' key='1' style={styles.shopcarIcon}></Link>
                {/* <p style={styles.ShowCart_TotalCount}>(0)</p> */}
                <p style={styles.ShowCart_TotalCount}>({shopcarlen})</p>

            </div> 
        </div>
      );
    }
}

const mapStateToProps = (state) => {
    return {
        shopcarlen: state.ShopCar.length
    }
}

export default connect(mapStateToProps)(Header);
